mapper.insert:
/*sample.service.impl.CodeMapper.saveCodeMst*/
INSERT INTO CMMN_CD_MST
( CODE
, CODE_NM
)
VALUES 
( #{CODE}
, #{CODE_NM}
)
ON DUPLICATE KEY 
UPDATE CODE_NM = #{CODE_NM}

mapper.select:
/*sample.service.impl.CodeMapper.checkCodeMst*/
SELECT COUNT(1)
  FROM CMMN_CD_MST
 WHERE CODE = #{CODE}

mapper.insert:
/*sample.service.impl.CodeMapper.insertCodeMst*/
INSERT INTO CMMN_CD_MST
( CODE
, CODE_NM
)
VALUES 
( #{CODE}
, #{CODE_NM}
)

mapper.update:
/*sample.service.impl.CodeMapper.updateCodeMst*/
UPDATE CMMN_CD_MST
   SET CODE_NM = #{CODE_NM}
 WHERE CODE = #{CODE}

mapper.select:
/*sample.service.impl.CodeMapper.selectCodeDtl*/ SELECT CODE , CODE_NM FROM CMMN_CD_DTL WHERE 1=1
AND (CODE LIKE CONCAT('%',#{SEARCH_WORD},'%') OR CODE_NM LIKE CONCAT('%',#{SEARCH_WORD},'%') )
AND CODE LIKE CONCAT('%',#{SEARCH_WORD},'%')
AND CODE_NM LIKE CONCAT('%',#{SEARCH_WORD},'%')
AND PT_CODE = #{PT_CODE}

mapper.insert:
/*sample.service.impl.CodeMapper.saveCodeMst*/
INSERT INTO CMMN_CD_DTL
( CODE
, CODE_NM
, PT_CODE
)
VALUES 
( #{CODE}
, #{CODE_NM}
, #{PT_CODE}
)
ON DUPLICATE KEY 
UPDATE CODE_NM = #{CODE_NM}

mapper.select:
/*sample.service.impl.CodeMapper.checkCodeDtl*/
SELECT COUNT(1)
  FROM CMMN_CD_DTL
 WHERE CODE = #{CODE}
   AND PT_CODE = #{PT_CODE}

mapper.insert:
/*sample.service.impl.CodeMapper.insertCodeDtl*/
