mapper.update:
UPDATE eval_guide
SET
question = #{question}
, upd_name = #{admin_name}
WHERE guide_code = #{guideCode}

mapper.select:
SELECT score, question
FROM eval_history
WHERE emp_code = #{empCode}
AND eval_date = #{evalDate}

SELECT DISTINCT a.emp_code as empCode, a.name as name, DATE_FORMAT(b.eval_date, '%Y-%m-%d') as evalDate, b.total_score as totalScore, b.eval_grade as evalGrade, b.reg_name as regName, c.guide_code as guideCode FROM employee a JOIN evaluation b ON a.emp_code = b.emp_code JOIN eval_history c ON b.emp_code = c.emp_code WHERE 1=1 AND guide_code = 1 AND a.mng_code = #{admin_code}
AND ( a.emp_code LIKE CONCAT('%', #{Word}, '%') OR a.name LIKE CONCAT('%', #{Word}, '%') )
AND a.emp_code LIKE CONCAT('%', #{Word}, '%')
AND a.name LIKE CONCAT('%', #{Word}, '%')
AND b.eval_date BETWEEN #{StartDate} AND #{EndDate}
