mapper.select:
SELECT hourly 
FROM `assignment` a 
WHERE a.assign_code = #{assign_code};

SELECT emp_code, name, a.assign_code, assign_name, dep_name, join_date, IFNULL(resign_date, "") resign_date, account FROM employee e JOIN `assignment` a ON e.assign_code = a.assign_code JOIN department d ON e.dep_code = d.dep_code
WHERE join_date < DATE_FORMAT(CURDATE(), '%Y-%m-01')
AND (resign_date >= DATE_FORMAT(DATE_SUB(CURDATE(), INTERVAL 1 MONTH), '%Y-%m-01') OR resign_date IS NULL);
AND a.assign_code = #{assign_code}

SELECT IF(e.resign_date IS NULL OR MONTH(resign_date) >= MONTH(CURDATE())
		, a.monthly 
		, (SELECT	
			(SELECT COUNT(*)
				FROM attendance a JOIN att_code ac 
				ON a.atten_code = ac.atten_code 
				JOIN employee e 
				ON a.emp_code = e.emp_code 
				WHERE a.emp_code =  #{emp_code}
				AND (a.atten_code = 1 OR a.atten_code = 3)
				AND YEAR(work_date) = YEAR(DATE_SUB(NOW(), INTERVAL 1 MONTH))
				AND MONTH(work_date) = MONTH(DATE_SUB(NOW(), INTERVAL 1 MONTH))
			) 
			* 8 * a.hourly
		)		
) month
FROM `assignment` a JOIN employee e 
ON a.assign_code = e.assign_code 
WHERE a.assign_code = #{assign_code}	
AND emp_code = #{emp_code};

mapper.insert:
		INSERT INTO pay(emp_code
			, pay_year
			, pay_month
			, give_date
			, month
 			, pay_over
			, pay_meal
			, absence
			, pay_amount
			, income_tax
			, resident_tax
			, national_tax 
			, emp_insurance
			, health_insurance
			, longcare_insurance
			, actual_pay 
			, reg_name 
		)
		VALUES (#{emp_code}
			, YEAR(DATE_SUB(NOW(), INTERVAL 1 MONTH))
			, MONTH(DATE_SUB(NOW(), INTERVAL 1 MONTH))
			, #{give_date}
			, #{month}
 			, #{pay_over}
			, #{pay_meal}
			, #{absence}
			, #{pay_amount}
			, #{income_tax}
			, #{resident_tax}
			, #{national_tax}
			, #{emp_insurance}
			, #{health_insurance}
			, #{longcare_insurance}
			, #{actual_pay} 
			, "오명진"
		);
